export const TextAreaField = ({ label, value, setValue }) => {
return <>
  <label>{ label }</label>
  <textArea
    className="form-control"
    id="value"
    name="value"
    value={value}
    text={value}
    onChange={(event) => setValue(event.target.value)}
    />
</>;
}
