// import { getProductById } from "../api";
import { getProductById } from '../../api/productsAPI.js'
import { useEffect, useState } from "react";
import { ReviewList } from "./reviewList.jsx"
import { ReviewForm } from "./reviewForm.jsx"

export const ProductDetails = () => {

  const [ product, setProduct ] = useState(undefined);

  useEffect(() => { getProductById(1).then(x => {
      setProduct(x); }
    ); }, []);

  const newReview = ( review ) => {
    var tempProduct = {...product};
    tempProduct.reviews.push(review);
    setProduct(tempProduct);
  };
  if(!product){
    return <>Loading...</>;
  }
  return <>
    <div className="container-xxl pb-1">
      <nav className="mb-3 bg-opacity-25 navbar align-items-center bg-secondary justify-content-xxl-start rounded">
        <p className="ps-3 pt-1 navbar-item text-primary">Tasty snacks<span className="text-black-50"> / {product.name}</span></p>
      </nav>
    <div className="container-fluid">
      <div className="row justify-content-sm-left">
        <div className="col-auto">
          <img src={product.imageUrl}/>
        </div>
        <div className="col d-flex align-items-center">
          <div className="container-fluid">
            <div className="row">
              <div className="col">
                <h1> { product.name } </h1>
                <div className="badge bg-primary text-wrap fs-5 mb-3">
                  ${ product.price }
                </div>
                <p> { product.description } </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
    <h5>Product Reviews<span className="text-secondary"> ({product.reviews.length})</span></h5>
    <ReviewList reviews={product.reviews}/>
    <ReviewForm onReviewAdded={newReview}/>
    </div>
  </>;
};
