export const TextAreaField = ({ label, value, setValue }) => {
return <>
  <div className="form-group mb-3">
  <label htmlFor="value">{ label }</label>
  <textArea
      id="value"
      name="value"
      value={value}
      onChange={ event => setValue(event.target.value) }
      className="form-control" ></textArea>
</div>
</>;
}
