// import { getProductById } from "../api";
import { getProductById } from '../../api/productsAPI.js'
import { useEffect, useState } from "react";
import { ReviewList } from "./reviewList.jsx"
import { ReviewForm } from "./reviewForm.jsx"

export const ProductDetails = () => {

  const [ product, setProduct ] = useState(undefined);

  useEffect(() => {
    getProductById(1).then(x => {
      setProduct(x);
    });
  }, []);

  const newReview = ( review ) => {
    var tempProduct = {...product};
    tempProduct.reviews.push(review);
    setProduct(tempProduct);
  };
  if(!product){
    return <>Loading...</>;
  }
  return <>
    <div className="container-xxl">
      <nav className="p-2 mb-2 bg-opacity-75 navbar align-items-center bg-secondary justify-content-xxl-start rounded">
        <p className="navbar-item text-primary">Tasty snacks<span className="text-black-50"> / {product.name}</span></p>
      </nav>
    <div className="container-fluid">
      <img src={product.imageUrl}/>
        <h5> { product.name } </h5>
        <h5> { product.price } </h5>
      <p> { product.description } </p>
    </div>
    <h5>Product Reviews<span className="text-secondary"> ({product.reviews.length})</span></h5>
    <ReviewList reviews={product.reviews}/>
    <ReviewForm onReviewAdded={newReview}/>
    </div>
  </>;
};
