export const SelectField = ({ label, value, setValue, options, optionValueKey, optionLabelKey, hideBlankOption}) => {
    return <div className="form-group mb-3">
    <label htmlFor="value">{label}</label>
    <select id="value"
        name="value"
        className="form-control"
        value={value}
        onChange={event => setValue(event.target.value)}>
            { !hideBlankOption && <option></option> }
            {
                options && options.map((value, label) =>(
                    <option value={optionValueKey ? option[optionValueKey].value : option}>
                        {optionLabelKey ? option[optionLabelKey].label  : optionValueKey ? option[optionValueKey].value : option }
                    </option>))
            }
    </select>
</div>;
}
