import { useEffect, useState } from "react";
import { TextField } from "../common/textField.jsx";
import { SelectField } from "../common/selectField.jsx";
import { TextAreaField } from "../common/textAreaField.jsx";

export const ReviewForm = ({ onReviewAdded }) => {
  const [ userName, setUserName ] = useState('');
  const [ rating, setRating ] = useState('');
  const [ comment, setComment ] = useState('');
  const [ ratingOptions ] = useState(
     [ { value: 1, label: '1 stars' },
       { value: 2, label: '2 stars' },
       { value: 3, label: '3 stars' },
       { value: 4, label: '4 stars' },
       { value: 5, label: '5 stars' } ]);

  return <>
    <h1>Add Review</h1>
    <TextField label="Name" value={userName} setValue={setUserName}/>
    <SelectField label="Rating"
    value={rating}
    setValue={setRating}
    options={ratingOptions}
    optionValueKey="id"
    optionLabelKey="name" />
    <TextAreaField label="Comment" value={comment} setValue={setComment} />
    <button type="button" onclick={() => {
        onReviewAdded({userName, rating, comment});
        userName = '';
        rating = '';
        comment = '';
      }
    }/>
  </>;
}
