import { getProductById } from "C:/Users/mailw/hw3/src/api/productsAPI.js";
import { useEffect, useState } from "react";

export const ProductDetails = () => {

  const [ product, setProduct ] = useState(getProductById(1));

  useEffect(() => {
        getProductById(1).then(x => setProduct(x));
    }, [ product ]);

    return <>
        <h1 value = { product }/>
    </>;
  return ;
};
