export const TextAreaField = ({ label, value, setValue }) => {
return <>
  <label>{ label }</label>
  <textArea
      id="value"
      name="value"
      value={value}
      onChange={(event) => setValue(event.target.value) }
      className="form-control"/>
</>;
}
