import { useEffect, useState } from "react";
import { TextField } from "../common/textField.jsx";
import { SelectField } from "../common/selectField.jsx";
import { Rating } from "../common/rating.jsx"
import { TextAreaField } from "../common/textAreaField.jsx";

export const ReviewForm = ({ onReviewAdded }) => {
  const [ userName, setUserName ] = useState('');
  const [ rating, setRating ] = useState('');
  const [ comment, setComment ] = useState('');
  const [ ratingOptions ] = useState(
     [ { value: 1, label: '1 star' },
       { value: 2, label: '2 stars' },
       { value: 3, label: '3 stars' },
       { value: 4, label: '4 stars' },
       { value: 5, label: '5 stars' } ]);

  return <>
    <nav className="p-2 text-white bg-opacity-75 navbar navbar-expand-lg bg-secondary rounded-top">
      <h3 className="ps-1">Add Review</h3>
    </nav>
    <div className="container rounded-bottom border">
      <TextField label="Name" value={userName} setValue={setUserName}/>
      <SelectField label="Rating"
      value={rating}
      setValue={setRating}
      options={ratingOptions}/>
      <Rating value={rating}/>
      <TextAreaField label="Comment" value={comment} setValue={setComment} />
      {(userName == '' || rating == '' || comment == '') && <button type="button" className="btn btn-md btn-primary" disabled>Submit</button>}
      {userName != '' && rating != '' && comment != '' && <button type="button" className="btn btn-md btn-primary" enabled onClick={() => {
          onReviewAdded({userName: userName, rating: rating, comment: comment, date: new Date()});
          setUserName('');
          setRating('');
          setComment('');
        }
      }>Submit</button>}

    </div>
  </>;
}
