export const ReviewList = ({ reviews }) => {
  var length = reviews.length;
  return <>
  <ul className="list-group mt-4 mb-2">
    <li className="list-group-item bg-primary text-white">
        Reviews
    </li>
    { length == 0 && <h1> Be the first to add a review! </h1> }
    { length > 0 && reviews.map((review, index) =>
    <li className="list-group-item" key={ index }>
        <h1> { review.rating } </h1>
        <h1> { review.name } </h1>
        <h1> { review.date } </h1>
        <h1> { review.comment } </h1>
    </li>) }
  </ul>
  </>;
}
