import { Rating } from "../common/rating.jsx"

export const ReviewList = ({ reviews }) => {
  return <>
  <ul className="list-group mt-4 mb-2">
    { reviews?.length == 0 && <h5 className="ms-3 mb-3"> Be the first to add a review! </h5> }
    { reviews?.length > 0 && reviews.map((review, index) =>
    <li className="list-group-item border" key={ index }>
      <Rating value={review.rating}/>
      <h1> { review.userName } </h1>
      <h1> { review.date.toDateString() } </h1>
      <h1> { review.comment } </h1>
    </li>) }
  </ul>
  </>;
}
